import React, { useState} from "react";
import "./App.css";

function App() {
  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [instructions, setInstructions] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const onNewUrlInput = (url) => {
    fetch(`http://localhost:8000/message?url=${url}`)
    .then((res) => res.json())
    .then((data) => {
      setTitle(data.title);
      setIngredients(data.ingredients);
      setInstructions(data.instructions);
    });
  }

  return (
    <div className="App" style={{whiteSpace: "pre-line"}}>
      <div className="registration">
        <h1>Registration</h1>
        <label>Username</label>
        <input type="text" /><br/>
        <label>password</label>
        <input type="text" /> <br />
        <button> Register</button>
      </div>
      <div className="login">
        <h1>Login</h1>
        <input type="text" placeholder="Username…" /> <br/>
        <input type="password" placeholder="Password…" />
        <button >Login</button>
      </div>

      <div className="recipe-scraper">
        <h1>Enter a website URL below to extract the recipe</h1>
        <input type="text" placeholder="URL" onChange={e => onNewUrlInput(e.target.value)} />
        <h1>{title}</h1>
        <h3>Ingredients:</h3>
        {ingredients}
        <h3>Instructions:</h3>
        {instructions}
      </div>
    </div>
  );
}

export default App
