import React, { useState} from "react";

import Axios from "axios";
import { connect } from "react-redux";



const ScraperPage = ({username}) => {

  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [instructions, setInstructions] = useState("");
  const [url, setUrl] = useState("");
  const [isSaved, setIsSaved] = useState(false);


  const onNewUrlInput = (url) => {
    setUrl(url);
    setIsSaved(false);
    fetch(`http://localhost:8000/message?url=${url}`)
    .then((res) => res.json())
    .then((data) => {
      setTitle(data.title);
      setIngredients(data.ingredients);
      setInstructions(data.instructions);
    });
  }



  const saveRecipe = () => {
    console.log("Hello");
    Axios.post("http://localhost:3001/save", {
      username: username,
      url: url,
      title: title,
      ingredients: ingredients,
      instructions: instructions, 
    }).then((response) => {
      if (!response.data.err) {
        setIsSaved(true);
      }
   });
  };


  return (
      <div className="recipe-scraper">
        {isLoggedIn && (
          <div>
            <button onClick={saveRecipe}>{isSaved ? "Saved!" : "Save Recipe"}</button>
            <button onClick={saveRecipe}>My Recipes</button>
          </div>
        )}
        <h1>Enter a website URL below to extract the recipe</h1>
        <input type="text" placeholder="URL" onChange={e => onNewUrlInput(e.target.value)} />
        <h1>{title}</h1>
        <h3>Ingredients:</h3>
        {ingredients}
        <h3>Instructions:</h3>
        {instructions}
      </div>
  );
}

const mapStateToProps = (state) => ({
  username: state.username
});

export default connect(mapStateToProps)(ScraperPage);
